// Query for making a call to the color API

// https://www.thecolorapi.com/scheme?hex=0047&mode=analogic&count=6

function getColour(hexCode, mode, numberOfColours) {

    var queryURL = 
    "https://www.thecolorapi.com/scheme?hex=" + hexCode +
    "&mode" + mode + "&count" + numberOfColours

    $.ajax({
        url: queryURL,
        method: "GET",
      }).then(function (response) {

        console.log(response)
    })
    };



export default getColour
